import React, { Component } from 'react';
import './login.css';
import { auth } from './firebase-config';
import { signInWithEmailAndPassword } from 'firebase/auth';

class Login extends Component {

    constructor() {
        super()
        this.state=({
            userName: '',
            userPassword: ''
        })
    };

    // updateFun= (newUpdate) =>{
    //     this.setState(newUpdate)
    //     console.log('>>>>>>',this.state)
    // };

    userNameFun = (e) => {
        this.setState({userName:e.target.value})
        // console.log(e.target.value)
    };

    userPasswordFun = (e) => {
        this.setState({userPassword:e.target.value})
        // console.log(e.target.value)
    };

    createLogin = async (e) => {

        e.preventDefault()
        try {
            const response = await signInWithEmailAndPassword(auth, this.state.userName, this.state.userPassword)
            console.log(response)
        } catch (error) {
            console.log(error)
        }
    };

    render() {
        return (
            <form className='form' onSubmit={this.createLogin}>
                <div className='inpDiv'>
                    <label htmlFor='userName'>Email :</label><br/>
                    <input
                        id='userName'
                        className='userInp'
                        name='userName'
                        placeholder='Enter your name here:'
                        onChange={this.userNameFun}
                        required
                    />
                </div>
                <div className='inpDiv'>
                    <label htmlFor='userPassword'>Password :</label><br/>
                    <input
                        id='userPassword'
                        className='userInp'
                        name='userPassword'
                        placeholder='Enter your password here:'
                        onChange={this.userPasswordFun}
                        required
                    />
                </div>
                <button>Submit !</button>
                {/* <p>{this.state.userName}</p>
                <p>{this.state.userPassword}</p> */}
            </form>
        )
    };
};

export default Login;
