import React, { Component } from "react";
import "./login.css";
import { auth } from "./firebase-config";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut,
} from "firebase/auth";

class Login extends Component {
  constructor() {
    super();
    this.userNameInp = React.createRef();
    this.userPasswordInp = React.createRef();
  }

  componentDidMount() {
    this.userNameInp.current.focus();
  }

  createLogin = async (e) => {
    e.preventDefault();
    // alert(`userN : ${this.userNameInp.current.value}\nuserP : ${this.userPasswordInp.current.value}`)
    try {
      const LOGIN = await signInWithEmailAndPassword(
        auth,
        this.userNameInp.current.value,
        this.userPasswordInp.current.value
      );
      // console.log(LOGIN.user.email)
      if (LOGIN.user.email) {
        console.log("login successful");
        // console.log(LOGIN)
      }
    } catch (error) {
      if (error.message.includes("auth/user-not-found")) {
        try {
          const SIGNUP = await createUserWithEmailAndPassword(
            auth,
            this.userNameInp.current.value,
            this.userPasswordInp.current.value
          );
          console.log("signup successful");
          // console.log(SIGNUP)
        } catch (error) {
          console.log(error.message);
        }
      } else {
        console.log(error.message);
      }
    }
  };

  logOut = async () => {
    try {
      const LOGOUT = await signOut(
        auth,
        this.userNameInp.current.value,
        this.userPasswordInp.current.value
      );
      console.log("logout successfully");
      console.log(LOGOUT);
    } catch (error) {
      console.log(error.message);
    }
  };

  render() {
    return (
      <>
        <form className="formTag" onSubmit={this.createLogin}>
          <h1>login?</h1>
          <input
            id="userName"
            className="userInp"
            placeholder=" Enter your name here:"
            ref={this.userNameInp}
            required
          />
          <input
            id="userPassword"
            className="userInp"
            placeholder=" Enter your password here:"
            ref={this.userPasswordInp}
            autoComplete="off"
            required
          />
          <button className="login">login?</button>
          <p className="forgot">forgotten password?</p>
          <hr></hr>
          <button className="signup">Sign up?</button>
          <button
            onClick={this.logOut}
            style={{ backgroundColor: "white", color: "blue" }}
          >
            SignOut?
          </button>
        </form>
      </>
    );
  }
}

export default Login;
