import './login.css'

class Login extends Component {

    constructor(props){
        super(props)
        this.state({
            userName:'',
            userPassword:''
        })
    };

    componentDidMount(){
        console.log(this.state.userName,this.state.userPassword)
    }

    updateFun= (newUpdate) =>{
        this.setState(newUpdate)
    };

    userNameFun= (e) =>{
        updateFun ({userName:e.targaet.value})
    };

    userPasswordFun= (e) =>{
        update({userPassword:e.targaet.value})
    };

    render() {
        const { userName, userPassword }=this.props;
        return (
            <form className='form' onSubmit={this.storeData}>
                <div className='inpDiv'>
                    <label htmlFor='userName' >Username/Email :</label><br></br>
                    <input 
                           id='userName' 
                           className='userInp' 
                           name='userName'
                           placeholder='Enter your name here:'
                           userName={userName}
                           required 
                    />
                </div>
                <div className='inpDiv'>
                    <label htmlFor='userPassword'>Password :</label><br></br>
                    <input 
                           id='userPassword' 
                           className='userInp' 
                           name='userPassword' 
                           placeholder='Enter your password here:' 
                           userPassword={userPassword}
                           required 
                    />
                </div>this.state.
                <button>Submit!</button>
            </form>
        )
    };
};

export default Login;
