import React, { Component } from 'react';
import './login.css';
import { auth } from './firebase-config';
import { createUserWithEmailAndPassword, signInWithEmailAndPassword } from 'firebase/auth';

class Login extends Component {

    constructor() {
        super()
        this.state=({
            userName: '',
            userPassword: ''
        })
    };

    userNameFun = (e) => {
        this.setState({userName:e.target.value})
        // console.log(e.target.value)
    };

    userPasswordFun = (e) => {
        this.setState({userPassword:e.target.value})
        // console.log(e.target.value)
    };

    createLogin = async (e) => {
        e.preventDefault()
        console.log(this.state)
        const response = await signInWithEmailAndPassword(auth, this.state.userName, this.state.userPassword)
        if(response.user.email){
            console.log('login successful')
        }else{
            const POST=await createUserWithEmailAndPassword(auth, this.state.userName, this.state.userPassword)

        }
        try {
            console.log(response.user.email)
        } catch (error) {
            console.log(error)
        }
    };

    render() {
        return (
        <>
            <form className='form' onSubmit={this.createLogin}>
                <div className='inpDiv'>
                    <label htmlFor='userName'>Email :</label><br/>
                    <input
                        id='userName'
                        className='userInp'
                        name='userName'
                        placeholder='Enter your name here:'
                        onChange={this.userNameFun}
                        required
                    />
                </div>
                <div className='inpDiv'>
                    <label htmlFor='userPassword'>Password :</label><br/>
                    <input
                        id='userPassword'
                        className='userInp'
                        name='userPassword'
                        placeholder='Enter your password here:'
                        onChange={this.userPasswordFun}
                        autoComplete='off'
                        required
                    />
                </div>
                <button>signUp? / login?</button>
                {/* <p>{this.state.userName}</p>
                <p>{this.state.userPassword}</p> */}
            </form>
            <button>SignOut?</button>
        </>
        )
    };
};

export default Login;
