import React, { Component } from 'react';
import './login.css';
impor

class Login extends Component {

    constructor() {
        super()
        this.state=({
            userName: '',
            userPassword: ''
        })
        console.log('constructor is called')
    };

    static getDerivedStateFromProps(){
        console.log('getDerivedStateFromProps called')
    };

    componentDidMount(e){
        console.log('componentDidMount is called')
    };

    updateFun= (newUpdate) =>{
        this.setState(newUpdate)
    };

    userNameFun = (e) => {
        this.updateFun({userName:e.target.value})
        // console.log(e.target.value)
    };

    userPasswordFun = (e) => {
        this.updateFun({userPassword:e.target.value})
        // console.log(e.target.value)
    };

    onSubmit = (e) =>{
        e.preventDefault()
        console.log('onSubmit called')
        console.log(this.state)
    };

    render() {
        console.log('render is called')
        return (
            <form className='form' onSubmit={this.onSubmit} >
                <div className='inpDiv'>
                    <label htmlFor='userName'>Username/Email :</label><br/>
                    <input
                        id='userName'
                        className='userInp'
                        name='userName'
                        placeholder='Enter your name here:'
                        onChange={this.userNameFun}
                        // userName={this.props.userName}
                        required
                    />
                </div>
                <div className='inpDiv'>
                    <label htmlFor='userPassword'>Password :</label><br/>
                    <input
                        id='userPassword'
                        className='userInp'
                        name='userPassword'
                        placeholder='Enter your password here:'
                        onChange={this.userPasswordFun}
                        // userPassword={this.props.userPassword}
                        required
                    />
                </div>
                <button>Submit !</button>
                {/* <p>{this.state.userName}</p>
                <p>{this.state.userPassword}</p> */}
            </form>
        )
    };
};

export default Login;
