import React, { Component } from 'react';
import './login.css';
import { auth } from './firebase-config';
import { createUserWithEmailAndPassword, signInWithEmailAndPassword, signOut } from 'firebase/auth';

class Login extends Component {

    constructor() {
        super()
        this.state=({
            userName: '',
            userPassword: ''
        })
    };

    userNameFun = (e) => {
        this.setState({userName:e.target.value})
        // console.log(e.target.value)
    };

    userPasswordFun = (e) => {
        this.setState({userPassword:e.target.value})
        // console.log(e.target.value)
    };

    createLogin = async (e) => {
        e.preventDefault()
        console.log(this.state)
        try {
            const LOGIN = await signInWithEmailAndPassword(auth, this.state.userName, this.state.userPassword)
            // console.log(LOGIN.user.email)
            if(LOGIN.user.email){
                console.log('login successful')
                // console.log(LOGIN)
            }
        } catch (error) {
            if(error.message.includes('auth/user-not-found')){
                try{
                    const SIGNUP = await createUserWithEmailAndPassword(auth, this.state.userName, this.state.userPassword)
                    console.log('signup successful')
                    // console.log(SIGNUP)
                } catch (error) {
                    console.log(error.message)
                }
            }else{
                console.log(error.message)
            }
        }
    };

    logOut = async () => {
        try{
            const LOGOUT = await signOut(auth, this.state.userName, this.state.userPassword)
            console.log('logout successfully')
            console.log(LOGOUT)
        } catch(error) {
            console.log(error.message)
        }
    };

    render() {
        return (
        <>
            <form className='form' onSubmit={this.createLogin}>
                <h1>login?</h1>
                <div className='inpDiv'>
                    <label htmlFor='userName'>Email :</label><br/>
                    <input
                        id='userName'
                        className='userInp'
                        name='userName'
                        placeholder=' Enter your name here:'
                        onChange={this.userNameFun}
                        required
                    />
                </div>
                <div className='inpDiv'>
                    <label htmlFor='userPassword'>Password :</label><br/>
                    <input
                        id='userPassword'
                        className='userInp'
                        name='userPassword'
                        placeholder=' Enter your password here:'
                        onChange={this.userPasswordFun}
                        autoComplete='off'
                        required
                    />
                </div>
                <button>signUp? / login?</button>
                {/* <p>{this.state.userName}</p>
                <p>{this.state.userPassword}</p> */}
            </form>
            <button onClick={this.logOut} style={{ borderBlock:'none', backgroundColor:'red' }}>SignOut?</button>
        </>
        )
    };
};

export default Login;
