  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut,
} from "firebase/auth";
import { async } from "@firebase/util";

class Login extends Component {
  constructor() {
    super();
    this.userNameInp = React.createRef();
    this.userPasswordInp = React.createRef();
  }

